var k = 0x01;
for(var i=0; i < 4; i++)
{
   for(var j=0; j < 8; j++)
   {
      var pid = new LiteralDataObj();
      pid.property = "PID " + k.toString(16);
      pid.valueIfTrue = "Supported";
      pid.valueIfFalse = "Unsupported";
      pid.value = BIT(i,(7-j)) ? true : false;                                 
      saveLiteralData(pid);
      k++;
   }
}


